<?phprequire_once $_SERVER['DOCUMENT_ROOT'].'/config/DB.php';class M_Admin{    public function __construct($goodId, $goodName, $goodPrice, $goodDiscr){        $this->goodId = $goodId;        $this->goodName = $goodName;        $this->goodPrice = $goodPrice;        $this->goodDiscr = $goodDiscr;    }    public function new_good(){        $sql = "INSERT INTO `goods`(`name`, `price`, `catalogImg`, `discription`, `id_category`, `status`) VALUES (:goodName, :goodPrice, '', :goodDiscr, null, null)";        $arg = ['goodName' => $this->goodName, 'goodPrice' => $this->goodPrice, 'goodDiscr' => $this->goodDiscr];        if ($arg['goodName'] && is_numeric($arg['goodPrice'])){            db::insert($sql, $arg);            $sql = "SELECT * FROM `goods`  order by `id_good` DESC LIMIT 1";            $newGood = db::getRow($sql,[]);            header('Location: index.php?act=view&c=goodedit&id_good=' . $newGood['id_good']);        } else header('Location: index.php?act=view&c=admin');    }    public function delete_good(){        $sql = "DELETE FROM goods where id_good = :goodId";        $arg = ['goodId' => $this->goodId];        db::delete($sql, $arg);    }    public function save_good(){        $sql = "UPDATE `goods` SET `name`=:goodName, `price`=:goodPrice, `discription`=:goodDiscr WHERE `id_good`=:goodId";        $arg = ['goodName' => $this->goodName, 'goodPrice' => $this->goodPrice, 'goodDiscr' => $this->goodDiscr, 'goodId' => $this->goodId];        db::update($sql, $arg);    }    public function load_img(){        $id_good = $_POST['id_good'];        $path = $_SERVER['DOCUMENT_ROOT']."/public/catalogImg/".$_FILES['img']['name'];        if(move_uploaded_file($_FILES['img']['tmp_name'],$path)){            $res = db::update('UPDATE `goods` SET `catalogImg`=:img WHERE `id_good`=:idGood',['img' => $_FILES['img']['name'], 'idGood' => $id_good]);            if($res) {                header('Location: ../public/index.php?act=view&c=goodedit&id_good='.$id_good);            }        } else            echo "Файл не выбран. Ошибка загрузки файла!<br>".                "<a href='../public/index.php?act=view&c=goodedit&id_good=" .$id_good ."'>Назад</a>";    }}